import React from 'react';
import '/home/tanmay/project/hospital-management-frontend/frontend-react/src/Components/styles.css';

const WelcomeComponent = () => {
  const today = new Date();
  const dateString = today.toLocaleDateString('en-US', {
    weekday: 'long',
    day: 'numeric',
    month: 'long',
    year: 'numeric',
  });

  return (
    <div className="welcome-container">
      <h1 className="welcome-title">Welcome to Medicover Admin Dashboard</h1>
      <p className="welcome-message">
        It's {dateString}. Get ready to manage patients, appointments, and staff
        efficiently and deliver the best possible healthcare experience.
      </p>
      <div className="dashboard-info">
        <span>
          Today's Appointments: <span className="highlight">32</span>
        </span>
        <span>
          Active Patients: <span className="highlight">1,548</span>
        </span>
        <span>
          Staff Members: <span className="highlight">237</span>
        </span>
      </div>
    </div>
  );
};

export default WelcomeComponent;
