import React, { useState } from 'react';
import '/home/tanmay/project/hospital-management-frontend/frontend-react/src/Components/styles.css';
import { retriveArchivePatientById } from '../api/DataRetriveApi';
import { retrivePatientById } from '../api/DataRetriveApi';

function PatientDetailsComponent() {
  const [patient,setPatient] = useState(null)
  const isArchive = useState(false)
  const { id, type } = useParams()


  function refreshPatientList() {
    if (type == 1) {
      retrivePatientById(id)
        .then(response =>{
            console.log(response)
            setPatient(response.data)
        })
        .catch(error => console.log(error))

    }else{
      retriveArchivePatientById(id)
        .then(response =>{
            console.log(response)
            setPatient(response.data)
            isArchive(true)
        })
        .catch(error => console.log(error))

    }

}

  return (
    <div className="patient-details">
      <h2>Patient Name {patient.patientName}</h2>
      <div className="info-grid">
        <div className="info-column">
          <p>ID {patient}</p>
          <p>Doctor {patient}</p>
          <p>Gender {patient}</p>
          <p>COVID Status {patient}</p>
          <p>Date of Birth {patient}</p>
          <p>Marital Status {patient}</p>
        </div>
        <div className="info-column">
          <p>Address {patient}</p>
          <p>Phone Number {patient}</p>
          <p>Email {patient}</p>
          <p>Medical History {patient}</p>
          <p>Blood Type </p>
        </div>
        <div className="info-column">
          <p>Current Symptoms</p>
          <p>Health Insurance Number</p>
          <p>Emergency Contact</p>
          <p>Admission Date</p>
          {}
        </div>
      </div>
    </div>
  );
}

export default PatientDetailsComponent;
