import React from 'react';
import '/home/tanmay/project/hospital-management-frontend/frontend-react/src/Components/styles.css';
import { useNavigate, useParams } from 'react-router-dom';
import { retrivePatientById } from '../api/DataRetriveApi';

function PatientDetailsUpdateCreateComponent() {

  const [patient,setPatient] = use({})
  const {id} = useParams()
  const navigate = useNavigate()
  useEffect(
    () => retrivePatient(),
    [id]
  )
  function retrivePatient() {
    if( id != -1 ){
      retrivePatientById(id)
      .then(
        response =>{
          console.log(response.data)
          setPatient(response.data)
        }
      )
      .catch(error =>console.log(error))
    }   
  }

  return (
    <div className='container'>
      <h2>Enter Patient Deatils</h2>

    <div className="patient-details">
      <h3>Patient Name</h3>
      <div className="info-grid">
        <div className="info-column">
          <p>ID</p>
          <p>Doctor</p>
          <p>Gender</p>
          <p>COVID Status</p>
          <p>Date of Birth</p>
          <p>Marital Status</p>
        </div>
        <div className="info-column">
          <p>Address</p>
          <p>Phone Number</p>
          <p>Email</p>
          <p>Medical History</p>
          <p>Blood Type</p>
        </div>
        <div className="info-column">
          <p>Current Symptoms</p>
          <p>Health Insurance Number</p>
          <p>Emergency Contact</p>
          <p>Admission Date</p>
        </div>
      </div>
    </div>
    </div>

  );
}

export default PatientDetailsUpdateCreateComponent;
