import React, { useEffect, useState } from 'react';
import './styles.css';
import { useNavigate, useParams } from 'react-router-dom';
import { createNewtask, retriveTaskById, updateTaskById } from '../api/DataRetriveApi';
import { Formik, Form, Field, ErrorMessage } from 'formik'
import * as Yup from 'yup';

function TaskUpdateCreateComponent() {

  const [task, settask] = useState({})
  const { id } = useParams()
  const navigate = useNavigate()
  useEffect(
    () => retriveTask(),
    [id]
  )
  function retriveTask() {
    if (id != -1) {
      retriveTaskById(id)
        .then(
          response => {
            console.log(response.data)
            settask(response.data)
          }
        )
        .catch(error => console.log(error))
    }
  }
  const validationSchema = Yup.object({
    taskName: Yup.string().required('task name is required'),
    taskGender: Yup.string().required('task Gender is required'),
    taskCovidStatus: Yup.string().required('task Covid satus is required'),
    doctorName: Yup.string().required('Doctor name is required'),
    taskphNumber: Yup.string().required('Phone number is required').matches(/^\d{10}$/, 'Must be 10 digits'),
    taskEmergencyContact: Yup.string().required('Phone number is required').matches(/^\d{10}$/, 'Must be 10 digits'),
    taskEmail: Yup.string().email('Invalid email format').required('Email is required'),
    taskBloodType: Yup.string().required('Blood type is required').oneOf(['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-']),
    taskDOB: Yup.date().required('Date of birth is required').max('2023-12-31', 'Date cannot be in the future'),
});

  function onSubmit(values) {
    if (id == -1) {
      const task = {
        taskName: values.taskName,
        doctorName: values.doctorName,
        taskGender: values.taskGender,
        taskCovidStatus: values.taskCovidStatus,
        taskDOB: values.taskDOB,
        taskMaritalS: values.taskMaritalS,
        taskAddress: values.taskAddress,
        taskphNumber: values.taskphNumber,
        taskEmail: values.taskEmail,
        taskMedicalHistory: values.taskMedicalHistory,
        taskCurrentSymptoms: values.taskCurrentSymptoms,
        taskBloodType: values.taskBloodType,
        taskHealthInsurancenumber: values.taskHealthInsurancenumber,
        taskEmergencyContact: values.taskEmergencyContact,
        taskID: id,
        taskAdmissionDate: "2023-12-26T19:56:33.979258"
      }
      console.log(task)
      createNewtask(task)
        .then(response => {
          // console.log(response)
          navigate('/tasks')
        })
        .catch(error => console.log(error))
    } else {
      updatetaskById(id, values)
        .then(response => {
          // console.log(response)
          navigate('/tasks')
        })
        .catch(error => console.log(error))
    }


  }

  return (
    <div className='container'>
      <Formik
        initialValues={task}
        enableReinitialize={true}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
        validateOnBlur={false}
        validateOnChange={false}
        >
        {
          () => (
            <Form>
              <div className="task-details">
                <fieldset>
                  <h3>Task Description
                  </h3>
                  <Field type='text' name='' />
                  <ErrorMessage name="" component="div" className="text-danger" />
                </fieldset>
                <div className="info-grid">
                  <div className="info-column">
                    <fieldset>
                      <p>
                        Skill:
                      </p>
                      <Field type='text' className="form-control" name='doctorName' />
                      <ErrorMessage name="doctorName" component="div" className="text-danger" />
                    </fieldset>
                    <fieldset >
                      <p>
                        Status is done -> true not done -> false:
                      </p>
                      <Field type='text' className="form-control" name='taskHealthInsurancenumber' />
                    </fieldset>
                  </div>
                  <div className="info-column">
                    <fieldset>
                      <p>
                      Priority:
                      </p>
                      <Field type='text' className="form-control" name='taskAddress' />
                    </fieldset>
                  </div>
                  <div className="info-column">
                    <fieldset>
                      <p>
                        Target Date:
                      </p>
                      <Field type='date' className="form-control" name='taskCurrentSymptoms' />
                    </fieldset>
                  </div>
                </div>



              </div>


              <div>
                <button type="submit" className="btn btn-success m-5">
                  {(id == -1) ? "Create new Task" : "Update"}
                </button>
              </div>

            </Form>

          )
        }


      </Formik>
    </div>

  );
}

export default TaskUpdateCreateComponent;
