                    <thead>
                        <tr>
                            <th>PatientID</th>
                            <th>PatientName</th>
                            <th>DoctorName</th>
                            <th>PatientGender</th>
                            <th>CovidStatus</th>
                            <th>Update</th>
                            <th>Details</th>
                            <th>Delete</th>
                        </tr>
